+
+/* Binder rings */
+.binder-ring {
+  position: absolute;
+  left: 2rem;
+  right: 2rem;
+  height: 20px;
+  transform: translateY(-50%);
+  pointer-events: none;
+  z-index: 100;
+}
+
+.hole-left, .hole-right {
+  position: absolute;
+  top: 50%;
+  transform: translateY(-50%);
+  width: var(--hole-diameter);
+  height: var(--hole-diameter);
+  border-radius: 50%;
+  background: #f5e6d3;
+  z-index: 102;
+}
+
+.hole-left {
+  left: calc(50% - var(--gap-size) - var(--bar-width) / 2 - var(--hole-diameter) / 2);
+}
+
+.hole-right {
+  left: calc(50% - var(--gap-size) + var(--bar-width) / 2 - var(--hole-diameter) / 2);
+}
+
+.ring-bar {
+  position: absolute;
+  left: calc(50% - var(--gap-size));
+  top: 50%;
+  transform: translate(-50%, -50%);
+  width: var(--bar-width);
+  height: var(--bar-height);
+  background: linear-gradient(180deg,
+    #666 0%,
+    #aaa 50%,
+    #666 100%
+  );
+  border-radius: calc(var(--bar-height) / 2);
+  z-index: 103;
 }
